<template> <div class="app" :class="mode"> <ChidiHeader :mode="mode" @toggle="toggle"> </ChidiHeader> <router-view /> <WorkspaceJsonFooter></WorkspaceJsonFooter> </div></template><script>import ChidiHeader from "./components/Header";import WorkspaceJsonFooter from "./components/Footer";import "animate.css";export default { name: "app", components: { ChidiHeader, WorkspaceJsonFooter, }, data() { return { mode: "white", }; }, methods: { toggle() { if (this.mode === "dark") { this.mode = "light"; } else { this.mode = "dark"; } }, },};</script><style>* { margin: 0; padding: 0; box-sizing: border-box;}.app { width: 100vw; min-height: 100vh; background-color: white; color: black !important;}.dark { background-color: black; color: white !important;}body { overflow-x: hidden;}</style>